// src/components/ForgotPassword/ForgotPassword.js

import React, { useState } from 'react';
import { Container, Title, Form, FormGroup, Label, Input, Button, Message } from './ForgotPassword';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/forgotpassword', { 
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      setMessage(data.message || 'Erro ao solicitar redefinição de senha');
    } catch (error) {
      console.error('Erro ao solicitar redefinição de senha:', error);
      setMessage('Erro ao solicitar redefinição de senha. Tente novamente.');
    }
  };

  return (
    <Container>
      <Title>Redefinir Senha</Title>
      <Form onSubmit={handleSubmit}>
        <FormGroup>
          <Label htmlFor="email">E-mail:</Label>
          <Input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </FormGroup>
        <Button type="submit">Enviar E-mail de Redefinição de Senha</Button>
      </Form>
      {message && <Message>{message}</Message>}
    </Container>
  );
};

export default ForgotPassword;
