import { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './Contas.module.css';

function Contas() {
  const [data, setData] = useState({ users: [], horarios: [] });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('/contas', { withCredentials: true })
      .then(response => {
        setData(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Erro ao carregar dados:', error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <div className={styles.container}>
      <h1>Contas</h1>
      <h2>Usuários:</h2>
      <ul>
        {data.users.map(user => (
          <li key={user._id}>
            <p>Nome: {user.username}</p>
            <p>Email: {user.email}</p>
            <p>Campus: {user.campus}</p>
            <p>Data de criação: {new Date(user.createdAt).toLocaleDateString()}</p>
          </li>
        ))}
      </ul>
      <h2>Horários:</h2>
      <ul>
        {data.horarios.map(horario => (
          <li key={horario._id}>
            <p>Nome: {horario.name}</p>
            <p>Categoria: {horario.category}</p>
            <p>Usuário: {horario.user.username}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Contas;
