import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Login.module.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loggedInUsername, setLoggedInUsername] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const storedIsLoggedIn = localStorage.getItem('isLoggedIn');
    const storedUsername = localStorage.getItem('username');
    if (storedIsLoggedIn === 'true' && storedUsername) {
      setIsLoggedIn(true);
      setLoggedInUsername(storedUsername);
    }
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        const errorMessage = await response.text();
        throw new Error(errorMessage || 'Erro ao efetuar login');
      }

      const data = await response.json();
      setIsLoggedIn(true);
      setLoggedInUsername(username);
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('username', username);
      localStorage.setItem('email', data.email);       
      localStorage.setItem('createdAt', data.createdAt); 
      alert('Login bem-sucedido!');

      window.location.reload();
    } catch (error) {
      alert(error.message);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setLoggedInUsername('');
    localStorage.removeItem('isLoggedIn');
    localStorage.removeItem('username');
    localStorage.removeItem('email');
    localStorage.removeItem('createdAt');
    alert('Logout realizado com sucesso!');
  };

  const handlePasswordReset = () => {
    navigate('/ForgotPassword');
  };

  const handleDeletCont = () => {
    navigate('/deletarConta');
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword); 
  };

  return (
    <div className={styles.logincontainer}>
      {isLoggedIn ? (
        <div className={styles.profilecard}>
          <div className={styles.usericon}>👤</div>
          <div className={styles.profileinfo}>
            <h3>{loggedInUsername}</h3>
            <p>Bem-vindo(a) de volta!</p>
          </div>
          <div>
            <button className={styles.logoutButton} onClick={handleLogout}>
              Logout
            </button>
            <button className={styles.resetPasswordButton} onClick={handlePasswordReset}>
              Redefinir Senha
            </button>
          </div>
        </div>
      ) : (
        <div>
          <h2>Login</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="username">Usuário:</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Digite o nome do usuário"
                required
              />
            </div>
            <div className={styles.passwordWrapper}>
              <label htmlFor="password">Senha:</label>
              <input
                type={showPassword ? 'text' : 'password'} 
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Digite a senha"
                required
              />
              <span
                className={styles.passwordToggleIcon}
                onClick={togglePasswordVisibility}
              >
                {showPassword ? '👁️' : '🙈'} 
              </span>
            </div>
            <button type="submit" className={styles.submitButton}>
              Entrar
            </button>
          </form>
          <button className={styles.resetPasswordButton} onClick={handlePasswordReset}>
            Redefinir Senha
          </button>
          <button className={styles.resetPasswordButton2} onClick={handleDeletCont}>
            Excluir conta          
          </button>
        </div>
      )}
    </div>
  );
};

export default Login;
