import React, { useState } from 'react';
import './ForgotPassword.module.css';

const ForgotPassword = () => {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/forgotpassword', { 
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      setMessage(data.message || 'Erro ao solicitar redefinição de senha');
    } catch (error) {
      console.error('Erro ao solicitar redefinição de senha:', error);
      setMessage('Erro ao solicitar redefinição de senha. Tente novamente.');
    }
  };

  return (
    <div className="container">
      <h2 className="title">Redefinir Senha</h2>
      <form onSubmit={handleSubmit} className="form">
        <div className="formGroup">
          <label htmlFor="email" className="label">E-mail:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="input"
          />
        </div>
        <button type="submit" className="button">Enviar E-mail de Redefinição de Senha</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default ForgotPassword;
